if (typeof msg.payload === "string") {
    // Se msg.payload for uma string direta (ex: "Jo√£o")
    msg.nome = msg.payload;
} else {
    // Se msg.payload for um objeto (ex: { nome: "Jo√£o" })
    // tenta pegar msg.payload.nome, ou coloca "Desconhecido" se n√£o existir
    msg.nome = msg.payload.nome;
}

msg.topic = "INSERT INTO clientes (nome) VALUES ($nome)";
msg.payload = [msg.nome];
return msg;

const gerarCodigo = (tam = 8) => {
    const c = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    return Array.from({ length: tam }, () => c[Math.floor(Math.random() * c.length)]).join("");
};

function dataHojeFormatada() {
    const hoje = new Date();

    const ano = hoje.getFullYear();
    const mes = String(hoje.getMonth() + 1).padStart(2, '0'); // meses come√ßam do 0
    const dia = String(hoje.getDate()).padStart(2, '0');

    return `${ano}-${mes}-${dia}`;
}

const codigo = gerarCodigo();
let data = dataHojeFormatada();

msg.codigoPedido = codigo;
msg.id_cliente = msg.payload[0].id_cliente;
msg.topic = "INSERT INTO pedidos (pedido_numero, id_cliente, data_pedido) VALUES ($codigoPedido, $id_cliente, $data)";
msg.payload = [msg.codigoPedido, msg.id_cliente, data];
return msg;

if (typeof msg.payload === "string") {
    // Se msg.payload for uma string direta (ex: "Jo√£o")
    msg.nome = msg.payload;
} else {
    // Se msg.payload for um objeto (ex: { nome: "Jo√£o" })
    // tenta pegar msg.payload.nome, ou coloca "Desconhecido" se n√£o existir
    msg.nome = msg.payload.nome;
}
msg.topic = "SELECT id_cliente FROM Clientes WHERE nome = $nome";
msg.payload = [msg.nome];  
return msg;

msg.payload = [
    msg.payload.pino1,
    msg.payload.pino2,
    msg.payload.pino3
];
return msg;

const nomesPorPino = msg.payload[0]; // ex: [ ["redonda", "hexagonal", "quadrada"], ... ]
const info_pedido = msg.payload[1];  // ex: [123] ‚Üí n√∫mero do pedido

const codigo_pedido = info_pedido[0];

const mapaItens = {
  "quadrada": 1,
  "hexagonal": 2,
  "redonda": 3
};

const resultado = [];

// Loop pelos pinos (√≠ndice de 0 a 2)
for (let i = 0; i < nomesPorPino.length; i++) {
  const nomes = nomesPorPino[i]; // ["redonda", "hexagonal", "quadrada"]
  const contagem = {};

  // Contar itens por nome
  for (const nome of nomes) {
    const nomeLower = nome.toLowerCase();
    if (mapaItens[nomeLower]) {
      contagem[nomeLower] = (contagem[nomeLower] || 0) + 1;
    }
  }

  // Criar inserts para este pino
  for (const [nome, quantidade] of Object.entries(contagem)) {
    const id_item = mapaItens[nome];
    const pino = i + 1; // Pino 1, 2, 3

    resultado.push({
      topic: "INSERT INTO Itens_Pedido (pedido_numero, id_item, quantidade, pino) VALUES ($codigo_pedido, $id_item, $quantidade, $pino)",
      payload: [codigo_pedido, id_item, quantidade, pino]
    });
  }
}

return [resultado];

let chatId = msg.payload.chatId;
let texto = msg.payload.content;
let estado = flow.get(chatId + "_estado");
let pedido = flow.get(chatId + "_pedido") || [];

if (texto === "/start") {
    flow.set(chatId + "_estado", "awaiting_name");
    flow.set(chatId + "_pedido", []);

    msg.payload = {
        chatId,
        type: "message",
        content: "Pedido iniciado! Qual √© o seu nome?"
    };
    return msg;
}

if (estado === "awaiting_name") {
    flow.set(chatId + "_nome", texto);
    flow.set(chatId + "_estado", "awaiting_item_1");

    msg.payload = {
        chatId,
        type: "message",
        content: `Ol√°, ${texto}! Escolha a primeira pe√ßa:`,
        options: {
            reply_markup: {
                inline_keyboard: [
                    [{ text: "Redonda", callback_data: "Redonda" }],
                    [{ text: "Hexagonal", callback_data: "Hexagonal" }],
                    [{ text: "Quadrada", callback_data: "Quadrada" }]
                ]
            }
        }
    };
    return msg;
}

return null; // ignora outras mensagens

const chatId = msg.payload.chatId;
const escolha = msg.payload.content;
const nome = flow.get(chatId + "_nome") || "Usu√°rio";

// Valida escolha recebida
const validas = ["Redonda", "Hexagonal", "Quadrada"];
if (!validas.includes(escolha)) {
    node.warn(`Escolha inv√°lida recebida: ${escolha}`);
    return [null, null];
}

// Inicializa estado e pedidos
let estado = flow.get(chatId + "_estado") || { pinoAtual: 1, etapa: 0 };
if (typeof estado !== "object" || ![1, 2, 3].includes(estado.pinoAtual) || typeof estado.etapa !== "number") {
    node.warn("Estado corrompido. Reinicializando.");
    estado = { pinoAtual: 1, etapa: 0 };
}

let pedidos = flow.get(chatId + "_pedidos") || { pino1: [], pino2: [], pino3: [] };
if (!Array.isArray(pedidos.pino1) || !Array.isArray(pedidos.pino2) || !Array.isArray(pedidos.pino3)) {
    node.warn("Pedidos corrompidos. Reinicializando.");
    pedidos = { pino1: [], pino2: [], pino3: [] };
}

// Debug logs
node.warn(`Chat ID: ${chatId}`);
node.warn(`Escolha recebida: ${escolha}`);
node.warn(`Estado atual: ${JSON.stringify(estado)}`);
node.warn(`Pedidos atuais: ${JSON.stringify(pedidos)}`);

// Adiciona a pe√ßa ao pino atual
const pinoKey = `pino${estado.pinoAtual}`;
if (!Array.isArray(pedidos[pinoKey])) {
    node.warn(`Pino ${pinoKey} n√£o √© um array. Inicializando.`);
    pedidos[pinoKey] = [];
}
pedidos[pinoKey].push(escolha);
estado.etapa += 1;

// Salva estado e pedidos
flow.set(chatId + "_estado", estado);
flow.set(chatId + "_pedidos", pedidos);

// Se completou 3 pe√ßas no pino atual
if (estado.etapa === 3) {
    const confirmacaoPino = {
        payload: {
            chatId,
            type: "message",
            content: `‚úÖ Pe√ßas escolhidas para o pino ${estado.pinoAtual}:\n- ${pedidos[pinoKey].join("\n- ")}`
        }
    };

    estado.pinoAtual += 1;
    estado.etapa = 0;

    // Se todos os pinos foram preenchidos
    if (estado.pinoAtual > 3) {
        const mensagemFinal = {
            payload: {
                chatId,
                type: "message",
                content: `üéâ Obrigado, ${nome}! Seu pedido foi registrado:\n\nüß© Pino 1: ${pedidos.pino1.join(", ")}\nüß© Pino 2: ${pedidos.pino2.join(", ")}\nüß© Pino 3: ${pedidos.pino3.join(", ")}`
            }
        };

        const mensagemInterna = {
            cliente: nome,
            chatId,
            payload: {
                pino1: pedidos.pino1,
                pino2: pedidos.pino2,
                pino3: pedidos.pino3
            }
        };

        node.warn("Pedido finalizado. Dados enviados internamente:");
        node.warn(JSON.stringify(mensagemInterna));

        // Limpa estado e pedidos
        flow.set(chatId + "_estado", null);
        flow.set(chatId + "_pedidos", null);

        return [[confirmacaoPino, mensagemFinal], mensagemInterna];
    }

    // Pr√≥ximo pino
    flow.set(chatId + "_estado", estado);
    flow.set(chatId + "_pedidos", pedidos);

    const novaPergunta = {
        payload: {
            chatId,
            type: "message",
            content: `Vamos montar o pino ${estado.pinoAtual}. Escolha a primeira pe√ßa:`,
            options: {
                reply_markup: {
                    inline_keyboard: [
                        [{ text: "Redonda", callback_data: "Redonda" }],
                        [{ text: "Hexagonal", callback_data: "Hexagonal" }],
                        [{ text: "Quadrada", callback_data: "Quadrada" }]
                    ]
                }
            }
        }
    };

    return [[confirmacaoPino, novaPergunta], null];
}

// Ainda h√° pe√ßas pendentes no pino atual
msg.payload = {
    chatId,
    type: "message",
    content: `Escolha a pr√≥xima pe√ßa para o pino ${estado.pinoAtual}:`,
    options: {
        reply_markup: {
            inline_keyboard: [
                [{ text: "Redonda", callback_data: "Redonda" }],
                [{ text: "Hexagonal", callback_data: "Hexagonal" }],
                [{ text: "Quadrada", callback_data: "Quadrada" }]
            ]
        }
    }
};

return [msg, null];

const chatId = msg.chatId;
const nome = msg.cliente;
const payload = msg.payload || {};

// Valida a estrutura do payload
if (!Array.isArray(payload.pino1) || !Array.isArray(payload.pino2) || !Array.isArray(payload.pino3)) {
    node.warn(`Payload inv√°lido. Estrutura esperada: { pino1: [], pino2: [], pino3: [] }. Recebido: ${JSON.stringify(payload)}`);
    return [null, null];
}

if (payload.pino1.length !== 3 || payload.pino2.length !== 3 || payload.pino3.length !== 3) {
    node.warn(`Cada pino deve conter exatamente 3 pe√ßas. Recebido: pino1=${payload.pino1.length}, pino2=${payload.pino2.length}, pino3=${payload.pino3.length}`);
    return [null, null];
}

// Debug logs
node.warn(`Chat ID: ${chatId}`);
node.warn(`Nome: ${nome}`);
node.warn(`Pedidos recebidos: ${JSON.stringify(payload)}`);

// Prepara mensagens de sa√≠da
const msgNome = {
    payload: nome
};

const msgItens = {
    payload: {
        pino1: payload.pino1,
        pino2: payload.pino2,
        pino3: payload.pino3
    }
};

return [msgNome, msgItens];
